import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AccountManagement.css';

const AccountManagement = () => {
    const [userData, setUserData] = useState({ name: '', email: '' });
    const [password, setPassword] = useState({ oldPassword: '', newPassword: '' });
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    // Function to fetch user details
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios
                .get('http://localhost:5000/account', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((response) => {
                    setUserData({ name: response.data.name, email: response.data.email });
                })
                .catch((error) => {
                    console.error('Error fetching user data', error);
                    setMessage('Error fetching user data.');
                });
        }
    }, [navigate]);

    // Function to handle user info update
    const handleInfoUpdate = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        try {
            const response = await axios.put(
                'http://localhost:5000/account/update',
                { name: userData.name, email: userData.email },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.status === 200) {
                setMessage('User information updated successfully.');
            }
        } catch (error) {
            setMessage('Failed to update user information.');
            console.error(error);
        }
    };

    // Function to handle password change
    const handlePasswordChange = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        try {
            const response = await axios.put(
                'http://localhost:5000/account/password',
                { oldPassword: password.oldPassword, newPassword: password.newPassword },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.status === 200) {
                setMessage('Password updated successfully.');
            }
        } catch (error) {
            setMessage('Failed to change password. Please check your old password.');
            console.error(error);
        }
    };

    return (
        <div className="account-management-container">
            <h2>Account Management</h2>

            {/* Edit User Information */}
            <form onSubmit={handleInfoUpdate} className="form-section">
                <h3>Update Personal Information</h3>
                <div className="form-group">
                    <label>Name</label>
                    <input
                        type="text"
                        value={userData.name}
                        onChange={(e) => setUserData({ ...userData, name: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Email</label>
                    <input
                        type="email"
                        value={userData.email}
                        onChange={(e) => setUserData({ ...userData, email: e.target.value })}
                        required
                    />
                </div>
                <button type="submit">Update Info</button>
            </form>

            {/* Change Password */}
            <form onSubmit={handlePasswordChange} className="form-section">
                <h3>Change Password</h3>
                <div className="form-group">
                    <label>Old Password</label>
                    <input
                        type="password"
                        value={password.oldPassword}
                        onChange={(e) => setPassword({ ...password, oldPassword: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>New Password</label>
                    <input
                        type="password"
                        value={password.newPassword}
                        onChange={(e) => setPassword({ ...password, newPassword: e.target.value })}
                        required
                    />
                </div>
                <button type="submit">Change Password</button>
            </form>

            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default AccountManagement;
