import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GroupModal } from './Modal';
import './Groups.css';

const Groups = () => {
    const [groups, setGroups] = useState([]);
    const [groupName, setGroupName] = useState('');
    const [description, setDescription] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Fetch the list of groups the user is part of when the component loads
        fetchGroups();
    }, []);

    // Function to fetch groups the user is part of
    const fetchGroups = async () => {
        const token = localStorage.getItem('token');
        try {
            const response = await axios.get('http://localhost:3113/groups', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setGroups(response.data);
        } catch (error) {
            console.error('Error fetching groups:', error);
            setMessage('Error fetching groups.');
        }
    };

    // Function to handle creating a new group
    const handleCreateGroup = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        try {
            const response = await axios.post(
                'http://localhost:3113/groups/create',
                { groupName, description },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.status === 201) {
                setMessage('Group created successfully!');
                setGroupName('');
                setDescription('');
                fetchGroups(); // Refresh the group list after creating a new group
            }
        } catch (error) {
            setMessage('Failed to create group. Please try again.');
            console.error('Error creating group:', error);
        }
    };

    // Function to handle deleting a group
    const handleDeleteGroup = async (groupId) => {
        const token = localStorage.getItem('token');
        if (window.confirm('Are you sure you want to delete this group? This action cannot be undone.')) {
            try {
                const response = await axios.delete(`http://localhost:3113/groups/${groupId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (response.status === 200) {
                    setMessage('Group deleted successfully!');
                    fetchGroups(); // Refresh the group list after deleting a group
                }
            } catch (error) {
                setMessage('Failed to delete group. Please try again.');
                console.error('Error deleting group:', error);
            }
        }
    };

    return (
        <div className="groups-container">
            <h2>Groups</h2>

            {/* Create Group Form */}
            <form onSubmit={handleCreateGroup} className="form-section">
                <h3>Create a New Group</h3>
                <div className="form-group">
                    <label>Group Name</label>
                    <input
                        type="text"
                        value={groupName}
                        onChange={(e) => setGroupName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Description</label>
                    <textarea
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Create Group</button>
            </form>

            {/* Display User's Groups */}
            <h3>Your Groups</h3>
            {groups.length > 0 ? (
                <ul className="group-list">
                    {groups.map((group) => (
                        <li key={group.group_id}>
                            <h4>{group.group_name}</h4>
                            <p>{group.description}</p>
                            {group.is_admin && (
                                <button
                                    onClick={() => handleDeleteGroup(group.group_id)}
                                    className="delete-button"
                                >
                                    Delete Group
                                </button>
                            )}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You are not part of any groups yet.</p>
            )}

            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default Groups;
